import React from 'react';
import './Home.css';

const Home = () => {
  return (
    <div className="profile">
      <h2 className="about-me-title">About Me</h2> {/* Agregar el encabezado "About Me" */}
      <img src="path_to_your_image.jpg" alt="Alberto Bort" className="profile-img" />
      <h1 className="profile-name">Alberto Bort <span className="linkedin-icon">üåê</span></h1>
      <p className="profile-title">Technical Business Analyst</p>
      <p className="profile-description">
        QA Engineer & IT Business Analyst with more than 6 years of experience in the IT sector across various industries such as banking and public transport & mobility, combining technical knowledge with management skills to thrive in various roles within organizations dedicated to software development.
      </p>
      <a href="path_to_your_cv.pdf" className="cv-link" download>Download My CV</a>
      <div className="languages">
        <span>Languages:</span>
        <img src="path_to_flag_image_1.png" alt="Language 1" />
        <img src="path_to_flag_image_2.png" alt="Language 2" />
        <img src="path_to_flag_image_3.png" alt="Language 3" />
      </div>
    </div>
  );
};

export default Home;
